create table t_import_requests (c_id bigint generated by default as identity, c_project_code varchar(255), c_user varchar(255), c_request_date timestamp, c_remote_file_path varchar(255), c_file_path varchar(255), c_data_type varchar(255), c_immediate smallint, c_status varchar(255), c_current_line integer, c_completed timestamp, primary key (c_id));
